import style from "./Card.module.css";
import { Link } from 'react-router-dom';
import { connect } from "react-redux";
import { addFavorite, removeFavorite} from "../redux/action"

function Card({ id, name, status, species, gender, origin, image, onClose, location }) {
   return (
      <div className={style.card}>
         <div className={style.front} >
            <img src={image} alt={name} />
         </div>


         <div className={style.back} >
            <div>
               <Link to={`/detail/${id}`} className={style.link}>
                  <h2 className={style.name}>{name}</h2>
               </Link>
            </div>

            <div className={style.species} >
               <h2>Specie: {species}</h2>
               <h2>Gender: {gender}</h2>
               <h2>Status: {status}</h2>
               <h2>Origin: {origin}</h2>
               <h2>Location: {location}</h2>
            </div>

            <div className={style.btn}>
               <button onClick={() => onClose(id)}>âœ˜</button>
            </div>
         </div>
         
      </div>
   );
}

const mapDispatchToProps = (dispatch) => {
   return {
      addFavorite: () => {dispatch(addFavorite())},
      removeFavorite: () => {dispatch(removeFavorite())}
   }
};

export default connect(null, mapDispatchToProps)(Card);
