import './App.css';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav';
import { useState } from 'react';
import axios from 'axios';

function App() {
   const [characters, setCharacters] = useState([]);

   const onSearch = (id, name) => {
      axios(`https://rickandmortyapi.com/api/character/?${id ? 'id=' + id : 'name=' + name}`)
         .then((response) => response.data)
         .then((data) => {
            if (data.results.length > 0) {
               const newCharacters = data.results.filter((character) => !characters.find((c) => c.id === character.id));
               if (newCharacters.length > 0) {
                  setCharacters((oldChars) => [...oldChars, ...newCharacters]);
               } else {
                  window.alert(`El personaje ya se encuentra en la lista`);
               }
            } else {
               window.alert(`No hay personajes con ${id ? 'id' : 'nombre'} ${id ? id : name}`);
            }
         });
   };
   

   const onClose = (id) => {
      setCharacters(characters.filter((characters) => characters.id !== id));
   }

   return (
      <div className='App'>
         <Nav onSearch={onSearch} />
         <Cards characters={characters} onClose={onClose}/>
      </div>
   );
}

export default App;
