import './index.css';
import './App.css';
import animationImage from './assets/img/animation.jpg';
import mortyImage from './assets/img/Morty.png';
import styled from 'styled-components';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav';
import About from './components/About/About';
import Detail from './components/Detail/Detail';
import Form from "./components/Form/Form";
import { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, useLocation, useNavigate  } from 'react-router-dom';

const URL_BASE = 'https://be-a-rym.up.railway.app/api/character';
const API_KEY = '4ad0439dd707.13a127d0fc3113ad4964';

const email = 'davidfelipe.fj@gmail.com';
const password = '1234qwer';
//
//
//
//


const Article = styled.article`
   position: relative;
   width: 250px;
   transition: all .3s ease;

   img:first-child {
      box-shadow: 0 60px 60px -60px rgba(0, 30, 255, 0.5);
      border-radius: 4px;
      object-fit: cover;
      width: 100%;
   }

   img:last-child {
      position: absolute;
      width: 200px;
      bottom: 0;
      left: 0;
      right: 0;
      margin: auto;
      transform: translateY(25%);
      transition: .3s ease;
      opacity: 0;
   }

   &:hover {
      transform: perspective(250px) rotateX(10deg) translateY(-5%) translateZ(0);
   }

   &::before {
      content: '';
      position: absolute;
      bottom: 0;
      height: 100%;
      width: 100%;
      background-image: linear-gradient(to bottom, transparent 10%, rgba(0, 0, 0, 0.5) 50%, rgba(0, 0, 0) 95%);
      opacity: 0;
      transition: all .3s ease;
   }

   &:hover::before {
      opacity: 1;
   }

   &:hover img:last-child{
      opacity: 1;
      transform: translateY(10%);
   }
`;

//
//
//
//

function App() {
   const location = useLocation();
   const navigate = useNavigate();
   const [characters, setCharacters] = useState([]);
   const [access, setAccess] = useState(false);

   const login = (userData) => {
      if(userData.email === email && userData.password === password){
         setAccess(true);
         navigate('/home');
      }
   }

   useEffect(() => {
      !access && navigate('/')
   }, [access, navigate])


   const onSearch = (id) => {

      const existingCharacter = characters.find((character) => character.id === id);
      if (existingCharacter) {
         window.alert('¡Esta carta ya ha sido abierta!');
      } else {
      axios(`${URL_BASE}/${id}?key=${API_KEY}`)
      .then(response => response.data)
      .then((data) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('¡No hay personajes con este ID!');
         }
      });
   }
};

   const onClose = (id) => {
      setCharacters(characters.filter((character) => character.id !== id))
   }

   return (
      
      <div className='App'>
          <Article>
            <img src={animationImg} alt="Rick And Morty" />
            <img src={mortyImg} alt="Morty" />
         </Article>
           {
            location.pathname !== '/' && <Nav onSearch={onSearch} />
         }

         <Routes>
            <Route path='/' element={<Form login={login}/>} />
            <Route path='/home' element={ <Cards characters={characters} onClose={onClose}/> }/>
            <Route path='/about' element={<About/>} />
            <Route path='/detail/:id' element={<Detail/>} />
         </Routes>
        
      </div>
   );
}

export default App;
