import './index.css';
import './App.css';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav';
import About from './components/About';
import Detail from './components/Detail';
import { useState } from 'react';
import axios from 'axios';
import { Routes, Route } from 'react-router-dom';

const URL_BASE = 'https://be-a-rym.up.railway.app/api/character';
const API_KEY = '4ad0439dd707.13a127d0fc3113ad4964';

function App() {
   const [characters, setCharacters] = useState([]);

   const onSearch = (id) => {

      const existingCharacter = characters.find((character) => character.id === id);
      if (existingCharacter) {
         window.alert('¡Esta carta ya ha sido abierta!');
      } else {
      axios(`${URL_BASE}/${id}?key=${API_KEY}`)
      .then(response => response.data)
      .then((data) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('¡No hay personajes con este ID!');
         }
      });
   }
};

   const onClose = (id) => {
      setCharacters(characters.filter((character) => character.id !== id))
   }

   return (
      <div className='App'>
         <Nav onSearch={onSearch} />

         <Routes>
            <Route path='/home' element={ <Cards characters={characters} onClose={onClose}/> }/>
            <Route path='/about' element={<About/>} />
            <Route path='/detail/:id' element={<Detail/>} />
         </Routes>
         <div className='animation-container'>
            <Spaceship />
         </div>
        
      </div>
   );
}

function Spaceship() {
   const [position, setPosition] = useState({ x: 0, y: 0 });
   const [speed, setSpeed] = useState({ x: 5, y: 5 });

   const updatePosition = () => {
      setPosition((prevPosition) => ({
         x: prevPosition.x + speed.x,
         y: prevPosition.y + speed.y
      }));

      if (position.x >= window.innerWidth - 150 || position.x <= 0) {
         setSpeed((prevSpeed) => ({
            x: -prevSpeed.x,
            y: prevSpeed.y
         }));
      }
      if (position.y >= window.innerHeight - 150 || position.y <= 0) {
         setSpeed((prevSpeed) => ({
            x: prevSpeed.x,
            y: -prevSpeed.y
         }));
      }
   };

   useEffect(() => {
      const interval = setInterval(updatePosition, 30);

      return () => clearInterval(interval);
   }, []);

   return (
      <div
         className='spaceship'
         style={{ top: `${position.y}px`, left: `${position.x}px` }}
      >
         <img src='assets/img/ship.png' alt='spaceship' />
      </div>
   );
}

export default App;
