import './App.css';
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav';
import { useState } from 'react';
import axios from 'axios';

function App() {
   const [characters, setCharacters] = useState([]);

   const onSearch = (type, value) => {
      axios(`https://rickandmortyapi.com/api/character/?${type}=${value}`)
         .then((response) => response.data)
         .then((data) => {
            if (data.results.length > 0) {
               setCharacters((oldChars) => [...oldChars, ...data.results]);
            } else {
               window.alert(`No hay personajes con ${type} ${value}`);
            }
         });
   };

   const onClose = (id) => {
      setCharacters(characters.filter((characters) => characters.id !== id));
   }

   return (
      <div className='App'>
         <Nav onSearch={onSearch} />
         <Cards characters={characters} onClose={onClose}/>
      </div>
   );
}

export default App;
