import { useState } from "react";
import validation from "./Validation";
import img1 from "../../assetsform/animation.jpg"
import img2 from "../../assetsform/Morty.png"
// import img3 from "../../assetsform/meeseeks.png"
// import img4 from "../../assetsform/ricksorp.png"
// import img5 from "../../assetsform/picklebazooka.png"
// import img6 from "../../assetsform/mrpoopy.png"
// import img7 from "../../assetsform/mortyscream.png"
// import img8 from "../../assetsform/meeseekskiller.png"
// import img9 from "../../assetsform/mortypirata.png"
// import img10 from "../../assetsform/picklerick.png"
// import img11 from "../../assetsform/mortyeatpickle.png"
// import img12 from "../../assetsform/rymportal.png"
// import img13 from "../../assetsform/rickfinger.png"
import styles from "./Form.module.css"

const Form = ({ login }) => {
    const [errors, setErrors] = useState({});
    const [userData, setUserData] = useState({
        email: '',
        password: ''
    });

    const handleChange = (event) => {
        setUserData({
            ...userData,
            [event.target.name]: event.target.value
        })

        setErrors(validation({
            ...userData,
            [event.target.name]: event.target.value
        }))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        login(userData);
    }

    return(
        <div>
        <form onSubmit={handleSubmit}>
            <label htmlFor="email" style={{ color: "white"}}>Email: </label>
            <input type="email" name='email' value={userData.email} onChange={handleChange}/>
            {errors.email && <p style={{ color: "red"}}>{errors.email}</p>}
            <hr />
            <label htmlFor="password" style={{ color: "white"}}>Password: </label>
            <input type="password" name="password" value={userData.password} onChange={handleChange}/>
            {errors.password && <p style={{ color: "red"}}>{errors.password}</p>}

            <button className={styles.submit}>Submit</button>
        </form>
        <div className={styles.article}>
             <img src={img1}alt="Rick And Morty"/>
             <img src={img2} alt="Morty"/>
             {/* <img src={img3} alt="meeseeks"/>
             <img src={img4} alt="ricksorp"/>
             <img src={img5} alt="picklebazooka"/>
             <img src={img6} alt="mrpoopy"/>
             <img src={img7} alt="mortyscream"/>
             <img src={img8} alt="meeseekskiller"/>
             <img src={img9} alt="mortypirata"/>
             <img src={img10} alt="picklerick"/>
             <img src={img11} alt="mortyeatpickle"/>
             <img src={img12} alt="rymportal"/>
             <img src={img13} alt="rickfinger"/> */}
        </div>
        </div>
    )
}

export default Form;
